import { MutableRefObject, ReactEventHandler, useRef, useState } from 'react'
import { Ratio } from 'react-bootstrap'
import {FaPlay} from 'react-icons/fa'
import {FaPause} from 'react-icons/fa'
function VideoPlayer({video} : any) {
     const videoRef = useRef() as MutableRefObject<HTMLVideoElement>
     const [isPlaying, setIsPlaying] = useState<boolean>(true)

     const pathToVideo : string = `http://localhost:8000/files/${video}`

     const playPause = () => {
          console.log(videoRef.current.currentTime)
          console.log(videoRef.current.duration)

          if (videoRef.current.paused) {
               videoRef.current.play()
               
               setIsPlaying(true)
          } else {
               videoRef.current.pause()
               setIsPlaying(false)
          }
     }

     const updateProgressBar = (e: ReactEventHandler<HTMLVideoElement>) => {

     }

     return (
          <>
               <Ratio aspectRatio='16x9' className='w-50 my-3 '>
                    <video onPlaying={updateProgressBar} onClick={playPause} ref={videoRef} playsInline autoPlay>
                         <source  src={pathToVideo} type="video/mp4" />
                    </video>
               </Ratio>
               <button className='btn success' onClick={playPause}>
                    {!isPlaying ? <FaPlay /> : <FaPause />}
               </button>
          </>
     )
   }

export default VideoPlayer
