import React, { Component } from 'react'
import { Button, FormControl, FormControlProps } from 'react-bootstrap'
import Form from 'react-bootstrap/Form'
export default class UploadForm extends Component {

     constructor(props: any) {
          super(props)
          this.state = {
               file: null
          }
     }

     updateFile = (file: File) => {
          this.setState((state) => ({file}))
     }
     render() {
          return (
               <Form.Group controlId="formFileLg" className="mb-3 flex">
                    <Form.Label>Large file input example</Form.Label>
                    <Form.Control onChange={ (e: React.ChangeEvent<HTMLInputElement>)  => {
                         if(!e.currentTarget.files) { return }
                         
                         const file = e.currentTarget.files[0]
                         this.updateFile(file)
                    } } className='mb-2' type="file" size="lg" />
                    <Button className='btn-success' type="submit">Submit form</Button>
               </Form.Group>
          )
     }
}
